    {() => {
        const popupDialog = () => {
          const core = new FormCore();
          Dialog.show({
            title: '标题',
            locale: 'zh',
            // 中文
            footerAlign: 'label',
            // label对齐形式，主要用于操作按钮对齐
            content: <Form core={core} layout={{
              label: 6,
              control: 18
            }} mdxType="Form">        
                        <FormItem label="username" name="username" mdxType="FormItem">
                            <Input mdxType="Input" />
                        </FormItem>
                    </Form>,
            onOk: (values, hide) => {
              return new Promise(resolve => {
                setTimeout(() => {
                  message.success('Success', 1, hide);
                }, 1000);
              });
            },
            onCancel: () => {}
          });
        };

        return <Button onClick={popupDialog} mdxType="Button">弹窗</Button>;
      }}
    </Playground>
