/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import react from 'react';
import Form, { FormItem, FormCore } from 'noform';
import { Input, Radio, Switch, InputNumber, Checkbox } from 'nowrapper/lib/antd';
import { TableRepeater, InlineRepeater, Selectify } from 'nowrapper/lib/antd/repeater';
import 'antd/dist/antd.css';
import 'noform/dist/index.css';
import 'nowrapper/dist/antd/index.css';
import 'nowrapper/dist/antd/repeater.css';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};
const RadioGroup = makeShortcode("RadioGroup");
const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "repeater-配置"
    }}>{`Repeater 配置`}</h1>
    <p>{`重复表单提供了非常多的配置项，您可以现在就开始试玩一番。`}</p>
    <h2 {...{
      "id": "核心配置"
    }}>{`核心配置`}</h2>
    <p>{`定义每一行的配置，如：`}<inlineCode parentName="p">{`<Repeater formConfig={formConfig} >`}</inlineCode>{` `}</p>
    <p>{`和 `}<a parentName="p" {...{
        "href": "/form/core/index#%E6%9E%84%E5%BB%BA%E5%8F%82%E6%95%B0"
      }}>{`FormCore构建参数`}</a>{` 完全一致，`}<strong parentName="p">{`校验`}</strong>{`，`}<strong parentName="p">{`状态管理`}</strong>{`...都通过此配置实现。`}</p>
    <h2 {...{
      "id": "基本配置"
    }}>{`基本配置`}</h2>
    <p>{`以下配置命名和实际使用一致，如：`}<inlineCode parentName="p">{`<Repeater locale="en">`}</inlineCode></p>
    <Playground __position={0} __code={'() => {\n  const { Group: RadioGroup } = Radio\n  const repeatValues = [\n    { username: \'lily\', gender: \'female\', id: \'lily\' },\n    { username: \'bobby\', gender: \'male\', id: \'bobby\' },\n  ]\n  const core = new FormCore({\n    values: {\n      repeater: repeatValues,\n    },\n    onChange: (firekeys, values, ctx) => {\n      const { locale, addPosition, itemAlign, maxLength } = values\n      if (firekeys.includes(\'locale\')) {\n        ctx.setItemProps(\'repeater\', { locale })\n      }\n      if (firekeys.includes(\'addPosition\')) {\n        ctx.setItemProps(\'repeater\', { addPosition })\n      }\n      if (firekeys.includes(\'itemAlign\')) {\n        ctx.setItemProps(\'repeater\', { itemAlign })\n      }\n\n      if (firekeys.includes(\'maxLength\')) {\n        ctx.setItemProps(\'repeater\', {\n          maxLength:\n            [null, undefined].indexOf(maxLength) !== -1\n              ? undefined\n              : maxLength,\n        })\n      }\n    },\n  })\n\n  const dataSource = [\n    { label: \'male\', value: \'male\' },\n    { label: \'female\', value: \'female\' },\n  ]\n  return (\n    <Form\n      core={core}\n      layout={{ label: 6, control: 18 }}\n      defaultMinWidth={false}\n    >\n      <FormItem label=\"语种 locale\" name=\"locale\" defaultValue=\"en\">\n        <RadioGroup\n          options={[\n            { label: \'en\', value: \'en\' },\n            { label: \'zh\', value: \'zh\' },\n          ]}\n        />\n      </FormItem>\n\n      <FormItem\n        label=\"添加按钮位置 addPosition\"\n        name=\"addPosition\"\n        defaultValue=\"top\"\n      >\n        <RadioGroup\n          options={[\n            { label: \'top\', value: \'top\' },\n            { label: \'bottom\', value: \'bottom\' },\n          ]}\n        />\n      </FormItem>\n\n      <FormItem\n        label=\"对齐方式 itemAlign\"\n        name=\"itemAlign\"\n        defaultValue=\"left\"\n      >\n        <RadioGroup\n          options={[\n            { label: \'left\', value: \'left\' },\n            { label: \'center\', value: \'center\' },\n            { label: \'right\', value: \'right\' },\n          ]}\n        />\n      </FormItem>\n\n      <FormItem\n        label=\"最大可添加条数 maxLength\"\n        defaultValue={3}\n        name=\"maxLength\"\n      >\n        <InputNumber />\n      </FormItem>\n\n      <FormItem label=\"TableRepeater\" name=\"repeater\">\n        <TableRepeater>\n          <FormItem label=\"username\" name=\"username\">\n            <Input style={{ width: 100 }} />\n          </FormItem>\n          <FormItem label=\"gender\" name=\"gender\">\n            <RadioGroup style={{ width: 200 }} options={dataSource} />\n          </FormItem>\n        </TableRepeater>\n      </FormItem>\n    </Form>\n  )\n}'} __scope={{
