    {() => {
        const {
          Group: RadioGroup
        } = Radio;
        const repeatValues = [{
          username: 'lily',
          gender: 'female',
          id: 'lily'
        }, {
          username: 'bobby',
          gender: 'male',
          id: 'bobby'
        }];
        const core = new FormCore({
          values: {
            repeater: repeatValues
          },
          onChange: (firekeys, values, ctx) => {
            const {
              locale,
              addPosition,
              itemAlign,
              maxLength
            } = values;

            if (firekeys.includes('locale')) {
              ctx.setItemProps('repeater', {
                locale
              });
            }

            if (firekeys.includes('addPosition')) {
              ctx.setItemProps('repeater', {
                addPosition
              });
            }

            if (firekeys.includes('itemAlign')) {
              ctx.setItemProps('repeater', {
                itemAlign
              });
            }

            if (firekeys.includes('maxLength')) {
              ctx.setItemProps('repeater', {
                maxLength: [null, undefined].indexOf(maxLength) !== -1 ? undefined : maxLength
              });
            }
          }
        });
        const dataSource = [{
          label: 'male',
          value: 'male'
        }, {
          label: 'female',
          value: 'female'
        }];
        return <Form core={core} layout={{
          label: 6,
          control: 18
        }} defaultMinWidth={false} mdxType="Form">
            <FormItem label="语种 locale" name="locale" defaultValue="en" mdxType="FormItem"><RadioGroup options={[{
              label: 'en',
              value: 'en'
            }, {
              label: 'zh',
              value: 'zh'
            }]} mdxType="RadioGroup" /></FormItem>

            <FormItem label="添加按钮位置 addPosition" name="addPosition" defaultValue="top" mdxType="FormItem"><RadioGroup options={[{
              label: 'top',
              value: 'top'
            }, {
              label: 'bottom',
              value: 'bottom'
            }]} mdxType="RadioGroup" /></FormItem>

            <FormItem label="对齐方式 itemAlign" name="itemAlign" defaultValue="left" mdxType="FormItem"><RadioGroup options={[{
              label: 'left',
              value: 'left'
            }, {
              label: 'center',
              value: 'center'
            }, {
              label: 'right',
              value: 'right'
            }]} mdxType="RadioGroup" /></FormItem>

            <FormItem label="最大可添加条数 maxLength" defaultValue={3} name="maxLength" mdxType="FormItem"><InputNumber mdxType="InputNumber" /></FormItem>

            <FormItem label="TableRepeater" name="repeater" mdxType="FormItem">
                <TableRepeater mdxType="TableRepeater">
                    <FormItem label="username" name="username" mdxType="FormItem"><Input style={{
                  width: 100
                }} mdxType="Input" /></FormItem>
                    <FormItem label="gender" name="gender" mdxType="FormItem"><RadioGroup style={{
                  width: 200
                }} options={dataSource} mdxType="RadioGroup" /></FormItem>
                </TableRepeater>
            </FormItem>
        </Form>;
      }}
    </Playground>
    <h2 {...{
      "id": "显示隐藏配置"
    }}>{`显示/隐藏配置`}</h2>
    <p>{`以下配置命名和实际使用一致，如：`}<inlineCode parentName="p">{`<Repeater hasAdd={false}>`}</inlineCode></p>
    <Playground __position={1} __code={'() => {\n  const { Group: RadioGroup } = Radio\n  const repeatValues = [\n    { username: \'lily\', gender: \'female\', id: \'lily\' },\n    { username: \'bobby\', gender: \'male\', id: \'bobby\' },\n  ]\n  const core = new FormCore({\n    values: {\n      repeater: repeatValues,\n    },\n    onChange: (firekeys, values, ctx) => {\n      const {\n        hasAdd,\n        hasUpdate,\n        hasDelete,\n        hasHeader,\n        hasDeleteConfirm,\n      } = values\n      if (firekeys.includes(\'hasAdd\')) {\n        ctx.setItemProps(\'repeater\', { hasAdd })\n      }\n      if (firekeys.includes(\'hasUpdate\')) {\n        ctx.setItemProps(\'repeater\', { hasUpdate })\n      }\n      if (firekeys.includes(\'hasDelete\')) {\n        ctx.setItemProps(\'repeater\', { hasDelete })\n      }\n      if (firekeys.includes(\'hasDelete\')) {\n        ctx.setItemProps(\'repeater\', { hasDelete })\n      }\n      if (firekeys.includes(\'hasHeader\')) {\n        ctx.setItemProps(\'repeater\', { hasHeader })\n      }\n      if (firekeys.includes(\'hasDeleteConfirm\')) {\n        ctx.setItemProps(\'repeater\', { hasDeleteConfirm })\n      }\n    },\n  })\n\n  const dataSource = [\n    { label: \'male\', value: \'male\' },\n    { label: \'female\', value: \'female\' },\n  ]\n  return (\n    <Form\n      core={core}\n      layout={{ label: 6, control: 18 }}\n      defaultMinWidth={false}\n    >\n      <div>\n        <div>\n          <FormItem\n            style={{ width: \'50%\' }}\n            layout={null}\n            inline\n            label=\"是否有添加 hasAdd\"\n            defaultValue={true}\n            name=\"hasAdd\"\n          >\n            <Checkbox />\n          </FormItem>\n          <FormItem\n            style={{ width: \'50%\' }}\n            layout={null}\n            inline\n            label=\"是否有更新 hasUpdate\"\n            defaultValue={true}\n            name=\"hasUpdate\"\n          >\n            <Checkbox />\n          </FormItem>\n        </div>\n        <div>\n          <FormItem\n            style={{ width: \'50%\' }}\n            layout={null}\n            inline\n            label=\"是否有删除 hasDelete\"\n            defaultValue={true}\n            name=\"hasDelete\"\n          >\n            <Checkbox />\n          </FormItem>\n          <FormItem\n            style={{ width: \'50%\' }}\n            layout={null}\n            inline\n            label=\"是否有头部 hasHeader\"\n            defaultValue={true}\n            name=\"hasHeader\"\n          >\n            <Checkbox />\n          </FormItem>\n        </div>\n        <FormItem\n          layout={null}\n          label=\"删除前确认弹窗 hasDeleteConfirm\"\n          name=\"hasDeleteConfirm\"\n          defaultValue={true}\n        >\n          <Checkbox />\n        </FormItem>\n      </div>\n\n      <FormItem label=\"TableRepeater\" name=\"repeater\">\n        <TableRepeater>\n          <FormItem label=\"username\" name=\"username\">\n            <Input style={{ width: 100 }} />\n          </FormItem>\n          <FormItem label=\"gender\" name=\"gender\">\n            <RadioGroup style={{ width: 200 }} options={dataSource} />\n          </FormItem>\n        </TableRepeater>\n      </FormItem>\n    </Form>\n  )\n}'} __scope={{
