    {() => {
        const core = new FormCore({
          enableReceiveProps: true
        });

        const statusHandler = e => core.setGlobalStatus(e.target.value); // 切换全局状态


        const opstHandler = e => {
          // 切换select数据源
          core.setItemProps('choice', {
            options: opts[e.target.value]
          });
        };

        const print = () => {
          console.log('values:', core.getValues());
          console.log('props:', core.getProps());
          console.log('status:', core.getStatus());
        };

        const opts = {
          opts1: [{
            label: '选项1',
            value: 'opt1'
          }],
          opts2: [{
            label: 'selection A',
            value: 'opt1'
          }]
        };
        return <Form core={core} mdxType="Form">
            <FormItem label="setGlobalStatus" render={() => {
            return <Radio.Group defaultValue="edit" onChange={statusHandler}>
                    <Radio.Button value="edit">编辑</Radio.Button>
                    <Radio.Button value="preview">预览</Radio.Button>
                    <Radio.Button value="disabled">禁用</Radio.Button>
                </Radio.Group>;
          }} mdxType="FormItem" />

            <FormItem label="setItemProps" render={() => {
            return <Radio.Group defaultValue="opts1" onChange={opstHandler}>
                    <Radio.Button value="opts1">数据源1</Radio.Button>
                    <Radio.Button value="opts2">数据源2</Radio.Button>
                </Radio.Group>;
          }} mdxType="FormItem" />

            <FormItem label="WrapperSelect" name="choice" defaultValue="opt1" mdxType="FormItem">
                <Select options={opts.opts1} mdxType="Select" />
            </FormItem>

            <FormItem label="UserName" name="username" defaultValue="Bobby" mdxType="FormItem">
                <Input mdxType="Input" />
            </FormItem>

            <FormItem listenProps label="Combo" render={(values, ctx) => {
            const {
              username,
              choice
            } = values;
            const {
              options
            } = ctx.getItemProps('choice');
            const {
              label
            } = options.find(item => item.value === choice);
            return <div style={{
              lineHeight: '28px'
            }}>
                    {username} 选择了 {label}
                    <Button onClick={print} mdxType="Button">打印</Button>
                </div>;
          }} mdxType="FormItem" />
        </Form>;
      }}
    </Playground>
    </MDXLayout>;
}

