    {() => {
        const propKeys = ['inline', 'defaultMinWidth', 'colon', 'full', 'inset'];
        const core = new FormCore({
          onChange: (fireKeys, values, ctx) => {
            const {
              direction,
              props
            } = values;

            if (fireKeys.includes('direction')) {
              core.setItemProps('form', {
                direction
              });
            } else if (fireKeys.includes('props')) {
              // 选中为true，没选为false
              const prop = {};
              propKeys.forEach(k => prop[k] = props.includes(k));
              core.setItemProps('form', prop);
            } else if (fireKeys.includes('layout-label')) {
              core.setItemValue('layout-control', 24 - values['layout-label']);
              core.setItemProps('form', {
                layout: {
                  label: values['layout-label'],
                  control: 24 - values['layout-label']
                }
              });
            } else if (fireKeys.includes('layout-control')) {
              core.setItemValue('layout-label', 24 - values['layout-control']);
              core.setItemProps('form', {
                layout: {
                  label: 24 - values['layout-control'],
                  control: values['layout-control']
                }
              });
            }
          }
        });
        return <Form core={core} mdxType="Form">
            <div style={{
            color: '#999'
          }}>点击一次改变属性，再点一次清空改变</div>
            <FormItem label="direction" name="direction" defaultValue="vertical" mdxType="FormItem">
                <Radio.Group>
                    <Radio.Button value="vertical">vertical</Radio.Button>
                    <Radio.Button value="horizontal">horizontal</Radio.Button>
                    <Radio.Button value="vertical-top">vertical-top</Radio.Button>
                </Radio.Group>
            </FormItem>

            <FormItem label="props" name="props" defaultValue={['colon']} mdxType="FormItem">
                <Checkbox.Group options={propKeys.map(k => ({
              label: k,
              value: k
            }))} />
            </FormItem>

            <If when={values => values.direction === 'vertical'} mdxType="If">
                <FormItem label="layout" mdxType="FormItem">
                    <div>
                        <FormItem defaultValue="6" label="label" name="layout-label" inline mdxType="FormItem">
                            <InputNumber mdxType="InputNumber" />
                        </FormItem>
                        <FormItem style={{
                  marginLeft: '8px'
                }} defaultValue="18" label="control" name="layout-control" inline mdxType="FormItem">
                            <InputNumber mdxType="InputNumber" />
                        </FormItem>
                    </div>
                </FormItem>
            </If>

            <Item listenProps render={(values, ctx) => {
            const props = ctx.getItemProps('form') || {};
            return <div style={{
              marginTop: '16px'
            }}>
                    <Form layout={{
                label: 6,
                control: 18
              }} {...props} mdxType="Form">
                        <FormItem label="name" name="name" mdxType="FormItem"><Input mdxType="Input" /></FormItem>
                        <FormItem label="age" name="age" mdxType="FormItem"><Input mdxType="Input" /></FormItem>
                    </Form>
                </div>;
          }} mdxType="Item" />
        </Form>;
      }}
    </Playground>
    <h2 {...{
      "id": "作为组件使用"
    }}>{`作为组件使用`}</h2>
    <p>{`将`}<strong parentName="p">{`Form`}</strong>{`作为组件可以轻松实现嵌套表单，如下代码所示。`}</p>
    <p>{`作为组件使用时，`}<inlineCode parentName="p">{`value`}</inlineCode>{`和`}<inlineCode parentName="p">{`onChange`}</inlineCode>{`一样被`}<strong parentName="p">{`FormItem`}</strong>{`自动代理掉，需要注意的是，此时无论是`}<inlineCode parentName="p">{`value`}</inlineCode>{`, `}<inlineCode parentName="p">{`props`}</inlineCode>{`, `}<inlineCode parentName="p">{`status`}</inlineCode>{`, `}<inlineCode parentName="p">{`error`}</inlineCode>{`都会变成`}<strong parentName="p">{`对象级别`}</strong>{`。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`<Form>
    <FormItem name="nestedForm" label="嵌套表单">
        <Form>
            <FormItem label="username" name="username"><Input /></FormItem>
            <FormItem label="age" name="age"><Input /></FormItem>
        </Form>
    </FormItem>
</Form>
`}</code></pre>
    <h2 {...{
      "id": "jsx属性"
    }}>{`JSX属性`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`属性名`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`作用`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`类型`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`默认值`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`direction`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`表单布局方式`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`ENUM('vertical', 'horizontal', 'vertical-top')`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`'vertical'`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`layout`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`表单栅格布局（继承）`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{ label: `}{`[0-24]`}{`, control: `}{`[0-24]`}{` }`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`null`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`defaultMinWidth`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`最小表单组件宽度(200px)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`布尔值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`full`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`layout激活下，组件是否占满control`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`布尔值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`inline`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`行内模式`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`布尔值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`inset`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`inset模式`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`布尔值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`colon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`label是否带 ':'`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`布尔值`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`value`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`表单值集合`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onChange`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`表单值变更时触发`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(value: {})`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`noop`}</td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
