/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import react from 'react';
import { Button, Input, Radio } from 'nowrapper/lib/antd';
import Form, { FormItem, FormCore } from 'noform';
import 'antd/dist/antd.css';
import 'noform/dist/index.css';
import 'nowrapper/dist/antd/index.css';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "formitem与item"
    }}>{`FormItem与Item`}</h1>
    <h2 {...{
      "id": "formitem是什么"
    }}>{`FormItem是什么`}</h2>
    <p><strong parentName="p">{`FormItem`}</strong>{` 的语义是 `}<strong parentName="p">{`表单组件`}</strong>{`。实现上是 `}<strong parentName="p">{`FormCore`}</strong>{` 的客户端，负责收发信息和处理表单组件的渲染。`}</p>
    <p>{`如下图所示， 功能完备的表单组件需要包含以下的结构(`}<inlineCode parentName="p">{`required`}</inlineCode>{`, `}<inlineCode parentName="p">{`label`}</inlineCode>{`, `}<inlineCode parentName="p">{`top`}</inlineCode>{`, `}<inlineCode parentName="p">{`prefix`}</inlineCode>{`, `}<inlineCode parentName="p">{`suffix`}</inlineCode>{`, `}<inlineCode parentName="p">{`help`}</inlineCode>{`, `}<inlineCode parentName="p">{`error`}</inlineCode>{`)`}</p>
    <p>{`实际的表单组件，诸如 `}<strong parentName="p">{`input`}</strong>{`, `}<strong parentName="p">{`select`}</strong>{` 都是位于图中的`}<inlineCode parentName="p">{`content`}</inlineCode>{`中。`}</p>
    <img alt="form-item slot" width="480" src="https://img.alicdn.com/tfs/TB1nAv2X8SD3KVjSZFqXXc4bpXa-948-334.png" />
    <h2 {...{
      "id": "item是什么"
    }}>{`Item是什么`}</h2>
    <p><strong parentName="p">{`Item`}</strong>{` 实际上是没有 `}<strong parentName="p">{`layout`}</strong>{`(`}<inlineCode parentName="p">{`prefix`}</inlineCode>{`, `}<inlineCode parentName="p">{`suffix`}</inlineCode>{`...) 的 `}<strong parentName="p">{`FormItem`}</strong>{` , 常常用来配合下面说的 `}<strong parentName="p">{`动态渲染`}</strong>{` 来实现值的拼装。`}</p>
    <img alt="form-item slot" width="100" src="https://img.alicdn.com/tfs/TB1E5UkX79E3KVjSZFGXXc19XXa-238-94.png" />
    <h2 {...{
      "id": "作用"
    }}>{`作用`}</h2>
    <p><strong parentName="p">{`FormItem`}</strong>{` 或 `}<strong parentName="p">{`Item`}</strong>{` 会自动绑定 `}<inlineCode parentName="p">{`value`}</inlineCode>{` 和 `}<inlineCode parentName="p">{`onChange`}</inlineCode>{` 等属性 到对应真实的表单组件，需要注意的是，真实的表单组件必须是单个组件。`}</p>
    <p>{`如需绑定自定义组件，请参照`}<a parentName="p" {...{
        "href": "/form/component/standard"
      }}>{`如何编写自定义组件`}</a></p>
    <h2 {...{
      "id": "引入方式"
    }}>{`引入方式`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`import Form, { FormItem, Item, FormCore, If } from 'noform';
`}</code></pre>
    <h2 {...{
      "id": "演示"
    }}>{`演示`}</h2>
