    {() => {
        const core = new FormCore(); // 初始化核心

        return <Form core={core} mdxType="Form">
            <div style={{
            color: '#999'
          }}>点击一次改变属性，再点一次清空改变</div>
            <FormItem label="setItemProps(text1)" render={() => {
            return <div>        
                    <Button onClick={() => {
                const {
                  required
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  required: !required
                });
              }} mdxType="Button">required</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  label
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  label: label ? null : '设置label'
                });
              }} mdxType="Button">label</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  prefix
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  prefix: prefix ? '' : '设置prefix'
                });
              }} mdxType="Button">prefix</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  suffix
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  suffix: suffix ? '' : '设置suffix'
                });
              }} mdxType="Button">suffix</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  top
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  top: top ? '' : '设置top'
                });
              }} mdxType="Button">top</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  help
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  help: help ? '' : '设置help'
                });
              }} mdxType="Button">help</Button>
                </div>;
          }} mdxType="FormItem" />
            <FormItem name="text1" mdxType="FormItem">
                <Input mdxType="Input" />
            </FormItem>
        </Form>;
      }}
    </Playground>
    <h2 {...{
      "id": "布局"
    }}>{`布局`}</h2>
    <ul>
      <li parentName="ul">{`默认无布局，根据长度自由撑开`}</li>
      <li parentName="ul">{`layout布局，如果 `}<strong parentName="li">{`Form`}</strong>{` 定义了 `}<inlineCode parentName="li">{`layout`}</inlineCode>{`，会继承此属性，如果需要取消布局，可以设置 `}<inlineCode parentName="li">{`layout={null}`}</inlineCode></li>
      <li parentName="ul">{`弹性布局， 设置 `}<inlineCode parentName="li">{`flex={true}`}</inlineCode>{`, 默认`}<inlineCode parentName="li">{`control`}</inlineCode>{`根据父容器长度自伸缩。可以设置`}<inlineCode parentName="li">{`labelWidth`}</inlineCode>{`属性自定义`}<inlineCode parentName="li">{`label`}</inlineCode>{`长度。`}</li>
      <li parentName="ul">{`full, 设置`}<inlineCode parentName="li">{`full={true}`}</inlineCode>{`，表单组件撑满`}<inlineCode parentName="li">{`control`}</inlineCode>{`空间`}</li>
    </ul>
