    {() => {
        const core = new FormCore({
          validateConfig: {
            text1: [{
              required: true,
              message: '字段1不能为空'
            }]
          }
        });
        return <Form core={core} mdxType="Form">
            <FormItem label="字段1" name="text1" mdxType="FormItem">
                <Input mdxType="Input" />
            </FormItem>
            <FormItem label="操作" render={() => {
            return <div>
                    <Button onClick={() => {
                core.validate();
              }} mdxType="Button">validate</Button>

                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                core.resetError();
              }} mdxType="Button">resetError</Button>
                </div>;
          }} mdxType="FormItem" />
        </Form>;
      }}
    </Playground>
    <h2 {...{
      "id": "onchange触发校验"
    }}>{`onChange触发校验`}</h2>
    <p>{`通过在核心构建函数中传入 `}<inlineCode parentName="p">{`autoValidate`}</inlineCode>{` 为 `}<inlineCode parentName="p">{`true`}</inlineCode>{` 启用onChange校验`}</p>
