    {() => {
        const core = new FormCore({
          validateConfig: {
            blurField: {
              required: true,
              message: '请填写blur字段'
            },
            changeField: {
              required: true,
              message: '请填写change字段'
            }
          }
        });

        const eventHandler = payload => {
          const {
            fireKey,
            fn,
            args
          } = payload;

          if (['blurField'].includes(fireKey)) {
            if (fn === 'onBlur') {
              core.validateItem(fireKey);
            }
          } else if (fn === 'onChange') {
            core.validateItem(fireKey);
          }
        };

        return <Form onEvent={eventHandler} core={core} mdxType="Form">
            <FormItem label="blur字段" name="blurField" mdxType="FormItem">
                <Input mdxType="Input" />
            </FormItem>

            <FormItem label="change字段" name="changeField" mdxType="FormItem">
                <DatePicker mdxType="DatePicker" />
            </FormItem>
        </Form>;
      }}
