    {() => {
        const core = new FormCore(); // 初始化核心

        return <Form core={core} mdxType="Form">
                <div style={{
            color: '#999'
          }}>点击一次改变属性，再点一次清空改变</div>
                <FormItem label="setItemProps(text1)" render={() => {
            return <div>                        
                        <Button onClick={() => {
                const {
                  placeholder
                } = core.getItemProps('text1');
                core.setItemProps('text1', {
                  placeholder: placeholder ? '' : '变更placeholder'
                });
              }} mdxType="Button">placeholder</Button>
                    <Button style={{
                marginLeft: '8px'
              }} onClick={() => {
                const {
                  options = []
                } = core.getItemProps('text1');
                const dataSource = [{
                  label: 'a',
                  value: 'a'
                }, {
                  label: 'b',
                  value: 'b'
                }];
                core.setItemProps('text1', {
                  options: options.length > 0 ? [] : dataSource
                });
              }} mdxType="Button">options</Button>

                    <Radio.Group style={{
                marginLeft: '8px'
              }} defaultValue="default" onChange={e => {
                core.setItemProps('text1', {
                  size: e.target.value
                });
              }}>
                        <Radio.Button value="small">小</Radio.Button>
                        <Radio.Button value="default">中</Radio.Button>
                        <Radio.Button value="large">大</Radio.Button>
                    </Radio.Group>
                </div>;
          }} mdxType="FormItem" />
            <FormItem label="字段1" name="text1" mdxType="FormItem">
                <Select mdxType="Select" />
            </FormItem>
        </Form>;
      }}
    </Playground>
